using DesertImage.Managers;using UnityEngine;namespace DesertImage{    public class ComponentUpdate : MonoBehaviour    {        private ManagerUpdate _managerUpdate;        #region mono behaviour methods        private void Update()        {            ManagerTick();        }        private void FixedUpdate()        {            ManagerFixedTick();        }        private void LateUpdate()        {            ManagerLateTick();        }        #endregion                #region public methods        public void Setup(ManagerUpdate managerUpdate)        {            SetUpdateManager(managerUpdate);        }        #endregion        #region init        private void SetUpdateManager(ManagerUpdate managerUpdate)        {            _managerUpdate = managerUpdate;        }        #endregion        #region manager updates        private void ManagerTick()        {            if(_managerUpdate == null) return;                        _managerUpdate.tick();        }        private void ManagerFixedTick()        {            if(_managerUpdate == null) return;                        _managerUpdate.fixedTick();        }        private void ManagerLateTick()        {            if(_managerUpdate == null) return;                        _managerUpdate.lateTick();        }        #endregion    }}