#if UNITY_EDITORusing UnityEditor;namespace DesertImage{    public static class CustomHotKeys    {        [MenuItem("Window/Custom/Lighting Settings %&l")]        private static void LightningWindow()        {            EditorApplication.ExecuteMenuItem("Window/Lighting/Settings");        }        [MenuItem("Window/Custom/Timeline %&t")]        private static void TimelineWindow()        {            EditorApplication.ExecuteMenuItem("Window/Timeline");        }        [MenuItem("Window/Custom/Profiler %&p")]        private static void ProfilerWindow()        {            EditorApplication.ExecuteMenuItem("Window/Profiler");        }        [MenuItem("Window/Custom/FrameDebugger %&f")]        private static void FrameDebuggerWindow()        {            EditorApplication.ExecuteMenuItem("Window/Frame Debugger");        }    }}#endif