using System.Collections.Generic;using UnityEngine;namespace DesertImage.Pools{    public class PoolGameObject : Pool<GameObject>    {        #region PRIVATE        private readonly Transform _parent;        #endregion        #region CONSTRUCTOR        public PoolGameObject(Transform parent)        {            _parent = parent;        }        #endregion        public GameObject getInstance(string baseName)        {            return Get(baseName);        }        public void returnInstance(GameObject obj, GameObject basePrefab)        {            ReturnInstance(obj, basePrefab);        }                public void returnInstance(GameObject obj, string baseName)        {            ReturnInstance(obj, baseName);        }        #region GET        private GameObject Get(string baseName)        {            GameObject obj = null;            foreach (var stack in CachedObjects.Values)            {                var poped = stack.Peek();                                if (poped.name != baseName + "(Clone)") continue;                obj = Get(poped);                                break;            }            return obj;        }        protected override void GetStuff(GameObject objInstance)        {            GetStuff(objInstance, null);        }        private void GetStuff(GameObject objInstance, Transform parent)        {            var poolable = objInstance.GetComponent<IPoolable>();            if (poolable != null)            {                poolable.onCreate();            }            objInstance.transform.parent = parent;            objInstance.SetActive(true);        }        protected override int GetHash(GameObject obj)        {            return obj.GetHashCode();        }        #endregion        protected void ReturnInstance(GameObject objInstance, GameObject basePrefab)        {            Stack<GameObject> objStack;            if (CachedObjects.TryGetValue(GetHash(basePrefab), out objStack))            {                objStack.Push(objInstance);            }            ReturnStuff(objInstance);        }                protected void ReturnInstance(GameObject objInstance, string baseName)        {            foreach (var stack in CachedObjects.Values)            {                var popedObj = stack.Peek();                                if(popedObj.name != baseName) continue;                                ReturnInstance(objInstance, popedObj);                                break;            }        }        #region CREATE / RETURN STUFF        protected override GameObject CreateInstance(GameObject objInstance)        {            var newObj = Object.Instantiate(objInstance, _parent);            ReturnStuff(newObj);            return newObj;        }        protected override void ReturnStuff(GameObject objInstance)        {            objInstance.SetActive(false);            objInstance.transform.parent = _parent;        }        #endregion    }}